Apperyio.AppPages = [{
    "name": "startScreen",
    "location": "startScreen.html"
}];

function startScreen_js() {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobiledatepicker_2': 'startScreen_mobiledatepicker_2',
        'mobileselectmenu_3': 'startScreen_mobileselectmenu_3',
        'mobileselectmenu_3-0': 'startScreen_mobileselectmenu_3-0',
        'mobiletextinput_10': 'startScreen_mobiletextinput_10',
        'mobileselectmenu_5': 'startScreen_mobileselectmenu_5',
        'mobileselectmenu_5-0': 'startScreen_mobileselectmenu_5-0',
        'mobileselectmenu_7': 'startScreen_mobileselectmenu_7',
        'mobileselectmenu_7-0': 'startScreen_mobileselectmenu_7-0',
        'mobiletextinput_13': 'startScreen_mobiletextinput_13',
        'mobilebutton_14': 'startScreen_mobilebutton_14'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    /*
     * Nonvisual components
     */

    Apperyio.mappings = Apperyio.mappings || {};

    Apperyio.datasources = Apperyio.datasources || {};

    Apperyio.CurrentScreen = 'startScreen';
    _.chain(Apperyio.mappings).filter(function(m) {
        return m.homeScreen === Apperyio.CurrentScreen;
    }).each(Apperyio.UIHandler.hideTemplateComponents);

    /*
     * Events and handlers
     */

    // On Load
    var startScreen_onLoad = function() {
            startScreen_elementsExtraJS();

            startScreen_deviceEvents();
            startScreen_windowEvents();
            startScreen_elementsEvents();
        };

    // screen window events


    function startScreen_windowEvents() {

        $('#startScreen').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    };

    // device events


    function startScreen_deviceEvents() {
        document.addEventListener("deviceready", function() {

        });
    };

    // screen elements extra js


    function startScreen_elementsExtraJS() {
        // screen (startScreen) extra code

        /* mobiledatepicker_2 */

        startScreen_mobiledatepicker_2_selector = "#startScreen_mobiledatepicker_2";
        startScreen_mobiledatepicker_2_dataPickerOptions = {
            dateFormat: "mm/dd/yy",
            firstDay: 0,

            maxDate: new Date(""),

            minDate: new Date("09/15/2015"),

            dayNamesMin: ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'],
            monthNames: ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'],
            showOtherMonths: true
        };

        startScreen_mobiledatepicker_2_dataPickerOptions.defaultDate = new Date("10/26/2015");

        Apperyio.__registerComponent('mobiledatepicker_2', new Apperyio.ApperyMobileDatePickerComponent("startScreen_mobiledatepicker_2", startScreen_mobiledatepicker_2_dataPickerOptions));

        /* mobileselectmenu_3 */

        $("#startScreen_mobileselectmenu_3").parent().find("a.ui-btn").attr("tabindex", "2");

        /* mobileselectmenu_5 */

        $("#startScreen_mobileselectmenu_5").parent().find("a.ui-btn").attr("tabindex", "3");

        /* mobileselectmenu_7 */

        $("#startScreen_mobileselectmenu_7").parent().find("a.ui-btn").attr("tabindex", "4");

    };

    // screen elements handler


    function startScreen_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });

    };

    $(document).off("pagebeforeshow", "#startScreen").on("pagebeforeshow", "#startScreen", function(event, ui) {
        Apperyio.CurrentScreen = "startScreen";
        _.chain(Apperyio.mappings).filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        }).each(Apperyio.UIHandler.hideTemplateComponents);
    });

    startScreen_onLoad();
};

$(document).off("pagecreate", "#startScreen").on("pagecreate", "#startScreen", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    startScreen_js();
});